from PIL import Image 
import subprocess
import os
import re

def cropPic(dst, save_dst):
    im = Image.open(dst)
    box = (2120, 1150, 2600, 1250)
    region = im.crop(box)
    region.save(save_dst)

def copyPic(dst, save_dst):
    im = Image.open(dst)
    im.save(save_dst)

